mapper.select:
SELECT

FROM
    t_activity t
    INNER JOIN t_activity_scope t1 ON t.id = t1.activity_id

        AND t1.fid IN

            #{value}





            INNER JOIN t_activity_classify t3 ON t.activity_classify_id = t3.id
            AND t3.id = #{params.activityClassifyId}



                INNER JOIN t_activity_classify t3 ON t.activity_classify_id = t3.id
                AND t3.name = #{params.activityClassifyName}



WHERE



            t.`status` = #{params.status}




                    t.`status` IN

                        #{value}



                    t.`status` IN ( 2, 3, 4 )





        AND t.activity_flag = #{params.flag}






                AND t.start_time BETWEEN minDateStr AND minDateStr




                        AND t.start_time >= #{params.minDateStr}


                        AND t.start_time <= #{params.maxDateStr}






        AND (
            t.`name` LIKE CONCAT('%', #{params.sw}, '%')
            OR t.organisers LIKE CONCAT('%', #{params.sw}, '%')
        )

GROUP BY
    t.id
ORDER BY
    FIELD( t.`status`, 2, 3, 4 )

        , t1.fid <> #{params.topFid}

    ,t.start_time DESC

SELECT
FROM t_activity t WHERE ( t.create_fid = #{params.topFid} OR( t.create_fid IN
#{value}
AND t.is_released = 1 ) )
AND t.`status` = #{params.status}
AND t.`status` > 0
AND ( t.name LIKE CONCAT('%', #{params.sw} , '%') OR t.create_user_name LIKE CONCAT('%', #{params.sw} , '%') OR t.create_org_name LIKE CONCAT('%', #{params.sw} , '%') )
GROUP BY t.id ORDER BY t.create_time DESC, t.create_fid <> #{params.topFid}

SELECT
FROM t_activity t WHERE t.create_uid = #{params.createUid} AND t.create_fid = #{params.createWfwfid}
AND t.`status` = #{params.status}
AND t.`status` > 0
AND ( t.name LIKE CONCAT('%', #{params.sw} , '%') OR t.create_user_name LIKE CONCAT('%', #{params.sw} , '%') OR t.create_org_name LIKE CONCAT('%', #{params.sw} , '%') )
GROUP BY t.id ORDER BY t.create_time DESC

SELECT , t2.`name` activityClassifyName FROM t_activity t INNER JOIN t_activity_scope t1 ON t.id = t1.activity_id AND t1.fid IN
#{value}
LEFT JOIN t_activity_classify t2 ON t.activity_classify_id = t2.id WHERE
t.`status` IN ( 2, 3, 4 )
AND t.activity_classify_id = #{params.classifyId}
AND #{params.date} BETWEEN t.start_date AND t.end_date
AND ( ( #{params.startDate} BETWEEN t.start_date AND t.end_date ) OR (#{params.endDate} BETWEEN t.start_date AND t.end_date ) OR ( #{params.startDate} <= t.start_date AND #{params.endDate} >= t.end_date ) )
AND t.activity_flag = #{params.activityFlag}
GROUP BY t.id ORDER BY FIELD( t.`status`, 3, 2, 4 )
, t1.fid <> #{params.topFid}
,t.start_time DESC

SELECT , t1.`name` activityClassifyName FROM t_activity t LEFT JOIN t_activity_classify t1 ON t.activity_classify_id = t1.id WHERE
t.create_fid = #{params.topFid}
AND t.activity_classify_id = #{params.classifyId}
AND t.`status` IN ( 2, 3, 4 )
AND #{params.date} BETWEEN t.start_date AND t.end_date
AND ( ( #{params.startDate} BETWEEN t.start_date AND t.end_date ) OR (#{params.endDate} BETWEEN t.start_date AND t.end_date ) OR ( #{params.startDate} <= t.start_date AND #{params.endDate} >= t.end_date ) )
AND t.activity_flag = #{params.activityFlag}
GROUP BY t.id ORDER BY FIELD( t.`status`, 3, 2, 4 ) ,t.start_time DESC

SELECT t.page_id FROM t_activity t INNER JOIN t_activity_scope t1 ON t.id = t1.activity_id AND t1.fid IN
#{value}
WHERE t.`status` <> 0

SELECT
FROM t_activity t WHERE t.create_fid = #{fid}
AND t.activity_flag = #{activityFlag}
AND t.`status` IN ( 2, 3, 4 ) ORDER BY FIELD( t.`status`, 3, 2, 4 ), t.start_time DESC

SELECT t.id
FROM t_activity t
WHERE t.start_date <= #{activityDate} and t.end_date >= #{activityDate}

SELECT t.activity_classify_id AS classifyId, tac.NAME AS typeName, count( 1 ) AS typeNum FROM t_activity t LEFT JOIN t_activity_classify tac ON t.activity_classify_id = tac.id WHERE t.id in
#{activityId}
GROUP BY t.activity_classify_id
