SELECT
, t4.is_released, t4.status, t4.is_top top
,t3.name activityClassifyName FROM t_activity t
INNER JOIN t_activity_scope t1 ON t.id = t1.activity_id
AND t1.fid IN
#{value}
LEFT JOIN t_classify t3 ON t.activity_classify_id = t3.id
INNER JOIN t_activity_market t4 ON t4.activity_id = t.id
LEFT JOIN t_activity_scope t5 ON t.id = t5.activity_id
INNER JOIN t_activity_tag t6 ON t.id = t6.activity_id AND t6.tag_id IN
#{value}
WHERE
t.`status` = #{params.status}
t.`status` IN
#{value}
t.`status` IN ( 2, 3 )
t.`status` IN ( 2, 3, 4 )
AND t.market_id = #{params.marketId}
AND t.activity_flag = #{params.flag}
AND t.start_time <= #{params.maxTimeStr} AND t.end_time >= #{params.minTimeStr}
AND #{params.minTimeStr} BETWEEN t.start_time AND t.end_time
AND #{params.maxTimeStr} BETWEEN t.start_time AND t.end_time
AND #{params.date} BETWEEN t.start_date AND t.end_date
AND t3.id = #{params.activityClassifyId}
AND t3.name = #{params.activityClassifyName}
AND t.activity_type = #{params.activityType}
AND ( t.`name` LIKE CONCAT('%', #{params.sw}, '%') OR t.organisers LIKE CONCAT('%', #{params.sw}, '%') OR t5.name LIKE CONCAT('%', #{params.sw}, '%') )
AND t.is_archived = 0 GROUP BY t.id ORDER BY
t4.is_top DESC,
FIELD( t.`status`, 2, 3, 4 ),
t1.fid <> #{params.topFid},
CASE WHEN t.`status` = 2 THEN t.start_time END ASC, CASE WHEN t.`status` = 3 OR t.`status` = 4 THEN t.start_time END DESC

