SELECT , t.start_time as startTime, t.end_Time as endTime FROM t_activity t
WHERE t.is_open_work = 1 AND t.work_id IS NOT NULL AND ( ( t.create_fid IN
#{value}
AND t.is_released = 1 ) OR t.create_fid = #{params.topFid} )
AND t.`status` = #{params.status}
AND t.`status` IN
#{value}
AND t.`status` IN ( 2, 3, 4 )
AND t.activity_flag = #{params.flag}
AND ( t.`name` LIKE CONCAT('%', #{params.sw}, '%') OR t.create_user_name LIKE CONCAT('%', #{params.sw} , '%') OR t.create_org_name LIKE CONCAT('%', #{params.sw} , '%') OR t.organisers LIKE CONCAT('%', #{params.sw}, '%') )
GROUP BY t.id ORDER BY t.create_time DESC

